// import './App.css';
import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";

const Title = styled.h1`font-size: 2em;`;

const Flex = styled.div`
    display: flex;
    justify-content: center;
    flex-direction: column;
`;

const FlexRow = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 16px;
`;

function App() {
  const [unit, setUnit] = useState("metric");

  console.log(unit);

  const handleUnitChange = event => {
    setUnit(event.target.value);
  };

  const [data, setData] = useState({
    temp: 10,
    humidity: 10,
    speed: 2
  });

  const [city, setCity] = useState("London");

  useEffect(
    () => {
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=281b1bf64dd901bf03d0cb8dbb2f18dd&units=${unit}`;
      axios
        .get(url)
        .then(res => {
          setData({
            ...data,
            temp: res.data.main.temp,
            mintemp: res.data.main.temp_min,
            maxtemp: res.data.main.temp_max,
            humidity: res.data.main.humidity,
            timezone: res.data.timezone
          });
          console.log(res.data.timezone);
        })
        //

        .catch(err => console.log(err));
    },
    [city]
  );

  useEffect(() => {
    const time = new Date();
    const timezones = Intl.DateTimeFormat().resolvedOptions().timeZone;
    const timezoneOffset = data.timezone / 3600;
    const timeZoneId = timezones.find(
      timeZone => new Date().toLocaleTimeString('en-US', { timeZone }).includes(timezoneOffset)
    );
    const localTime = time.toLocaleTimeString('en-US', { timeZone: timeZoneId });
    setData(prevData => ({
      ...prevData,
      localTime: localTime
    }));
  }, [data.timezone]);

  return (
    <Flex className="App">
      <select id="unit-select" value={unit} onChange={handleUnitChange}>
        <option value="metric">Metric</option>
        <option value="imperial">Imperial</option>
      </select>
      <div className="">
        <Title>
          {data.name}
        </Title>

        {data.localTime && <p>Local Time: {data.localTime}</p>}


        {/* list tempterature */}
        <FlexRow>
          <h4 className="grey">
            {data.mintemp} °C
          </h4>
          <Title className="">
            {data.temp} °C
          </Title>
          <h4 className="grey">
            {data.maxtemp} °C
          </h4>
        </FlexRow>

        <div className="icon-weather">
          <i>clud</i>
        </div>
        <div className="detail">
          <p>FEELS LIKE: </p>
          <p>HUMIDITY: </p>
          <p>WIND: </p>
        </div>
      </div>
    </Flex>
  );
}

export default App;
