// import './App.css';
import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";

const Title = styled.h1`font-size: 2em;`;

const Flex = styled.div`
    display: flex;
    justify-content: center;
    flex-direction: column;
`;

const FlexRow = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 16px;
`;

function App() {
  const [unit, setUnit] = useState("metric");

  console.log(unit);

  const handleUnitChange = event => {
    setUnit(event.target.value);
  };

  const [data, setData] = useState({
    temp: 10,
    humidity: 10,
    speed: 2
  });

  const [city, setCity] = useState("London");

  useEffect(
    () => {
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=281b1bf64dd901bf03d0cb8dbb2f18dd&units=${unit}`;
      axios
        .get(url)
        .then(res => {
          setData({
            ...data,
            temp: res.data.main.temp,
            mintemp: res.data.main.temp_min,
            maxtemp: res.data.main.temp_max,
            humidity: res.data.main.humidity,
            timezone: res.data.timezone,
            feels_like: res.data.main.feels_like,
            wind: res.data.wind.speed
          });
          console.log(res.data);
        })
        //

        .catch(err => console.log(err));
    },
    [city]
  );

  return (
    <Flex className="App">
      <select id="unit-select" value={unit} onChange={handleUnitChange}>
        <option value="metric">Metric</option>
        <option value="imperial">Imperial</option>
      </select>
      <div className="">
        <Title>
          {data.name}
        </Title>

        <p>Local Time: {data.timezone}</p>


        {/* list tempterature */}
        <FlexRow>
          <h4 className="grey">
            {data.mintemp} 째C
          </h4>
          <Title className="">
            {data.temp} 째C
          </Title>
          <h4 className="grey">
            {data.maxtemp} 째C
          </h4>
        </FlexRow>

        <div className="icon-weather">
          <i>clud</i>
        </div>
        <FlexRow className="detail">
          <p>FEELS LIKE: {data.feels_like} 째C</p>
          <p>HUMIDITY: {data.humidity} %</p>
          <p>WIND: {data.wind} </p>
        </FlexRow>
      </div>
    </Flex>
  );
}

export default App;
