// import './App.css';
import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";


const Title = styled.h1`
  font-size: 2em;
`;

const Flex = styled.div`
    display: flex;
    text-align: center;
    align-items: center;
    justify-content: center;
    flex-direction: column;
`;

const FlexRow = styled.div`
    display: flex;
    align-items: flex-end;
    justify-content: center;
    gap: 16px;
`;

function App() {
  const [unit, setUnit] = useState('metric');


  const handleUnitChange = event => {
    setUnit(event.target.value);
  };

  const [data, setData] = useState({
    temp: 10,
    humidity: 10,
    speed: 2,
    timezone: 0

  });

  const [localTime, setLocalTime] = useState(new Date());
  console.log(localTime)

  useEffect(() => {
    const interval = setInterval(() => {
      setLocalTime(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const utcTimestamp = localTime.getTime() + (localTime.getTimezoneOffset() * 60000);
  const offsetTimestamp = utcTimestamp + (data.timezone * 1000);
  const localTimeWithOffset = new Date(offsetTimestamp).toLocaleTimeString();


  const [city, setCity] = useState("");

  const handleInputChange = (event) => {
    setCity(event.target.value);
  };

  const handleSearch = () => {
    console.log(`Searching for city: ${city}`);
    // Call API or perform other actions with the city value
  };

  useEffect(
    () => {
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=281b1bf64dd901bf03d0cb8dbb2f18dd&units=${unit}`;
      axios
        .get(url)
        .then(res => {
          setData({
            ...data,
            temp: res.data.main.temp,
            mintemp: res.data.main.temp_min,
            maxtemp: res.data.main.temp_max,
            humidity: res.data.main.humidity,
            timezone: res.data.timezone,
            feels_like: res.data.main.feels_like,
            wind: res.data.wind.speed,
            city: res.data.name
          });
          console.log(res.data);
        })
        //

        .catch(err => console.log(err));
    },
    [city]
  );

  return (
    <Flex className="App">
      {/* <select id="unit-select" value={unit} onChange={handleUnitChange}>
        <option value="metric">Metric</option>
        <option value="imperial">Imperial</option>
      </select> */}
      <div className="">

        <div>
          <input
            type="text"
            placeholder="Enter city name"
            value={city}
            onChange={handleInputChange}
          />
          <button onClick={handleSearch}>Search</button>
        </div>

        <p>{localTimeWithOffset}</p>

        <Title>
          {data.city}
        </Title>



        {/* list tempterature */}
        <FlexRow>
          <Flex className="grey">
            <p className="m-0">LOW</p>
            <h2 className="m-0">
              {data.mintemp} 째C
            </h2>
          </Flex>

          <Title className="m-0"> {data.temp} 째C</Title>

          <Flex className="grey">
            <p className="m-0">HIGH</p>
            <h2 className="m-0">
              {data.maxtemp} 째C
            </h2>
          </Flex>
        </FlexRow>

        <div className="icon-weather">
        </div>
        <FlexRow className="detail">
          <p>FEELS LIKE: {data.feels_like} 째C</p>
          <p>HUMIDITY: {data.humidity} %</p>
          <p>WIND: {data.wind} m/s</p>
        </FlexRow>
      </div>
    </Flex>
  );
}

export default App;
