// import './App.css';
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Title = styled.h1`
  font-size: 2em;
`;

function App() {

  const [unit, setUnit] = useState('metric')

  console.log(unit)

  const handleUnitChange = (event) => {
    setUnit(event.target.value);
  };


  const [data, setData] = useState({
    temp: 10,
    city: 'London',
    humidity: 10,
    speed: 2
  })

  useEffect(() => {
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${data.city}&appid=281b1bf64dd901bf03d0cb8dbb2f18dd&units=${unit}`
    axios.get(url)
      .then(res => {
        setData({
          ...data,
          temp: res.data.main.temp,
          mintemp: res.data.main.temp_min,
          maxtemp: res.data.main.temp_max,
          city: res.data.name,
          humidity: res.data.main.humidity
        })
        console.log(res.data)
      })

      // 

      .catch(err => console.log(err));


  }, [data.city])

  const handleCityChange = (event) => {
    setData.city(event.target.value);
  };

  return (
    <div className="App">

      <div>
        <label htmlFor="city-search">Enter city name:</label>
        <input type="text" id="city-search" value={city} onChange={handleCityChange} />
      </div>

      <select id="unit-select" value={unit} onChange={handleUnitChange}>
        <option value="metric">Metric</option>
        <option value="imperial">Imperial</option>
      </select>
      <div className="">
        <Title>{data.name}</Title>
        <p>Clock</p>
        <div className='temp'>
          <h4 className='grey'>{data.mintemp}</h4>
          <Title className=''>{data.temp}</Title>
          <h4 className='grey'>{data.maxtemp}</h4>
        </div>
        <div className='icon-weather'><i>clud</i></div>
        <div className="detail">
          <p>FEELS LIKE: </p>
          <p>HUMIDITY: </p>
          <p>WIND: </p>
        </div>
      </div>
    </div>
  );
}

export default App;
