// import './App.css';
import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
import './App.css';
import { Form } from "react-bootstrap";


const Title = styled.h1`
  font-size: 2em;
`;

const Flex = styled.div`
    display: flex;
    text-align: center;
    align-items: center;
    justify-content: center;
    flex-direction: column;
`;

const FlexRow = styled.div`
    display: flex;
    align-items: flex-end;
    justify-content: center;
    gap: 16px;
`;

function App() {
  const [unit, setUnit] = useState('metric');

  const [data, setData] = useState({
    temp: 10,
    city: 'Tokyo',
    humidity: 10,
    speed: 2,
    timezone: 0,
    icons: ''

  });

  const [city, setCity] = useState('');
  const [error, setError] = useState('');

  const handleClick = () => {
    if (city !== "") {
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=281b1bf64dd901bf03d0cb8dbb2f18dd&units=${unit}`;
      axios
        .get(url)
        .then(res => {
          setData({
            ...data,
            temp: res.data.main.temp,
            mintemp: res.data.main.temp_min,
            maxtemp: res.data.main.temp_max,
            humidity: res.data.main.humidity,
            timezone: res.data.timezone,
            feels_like: res.data.main.feels_like,
            wind: res.data.wind.speed,
            city: res.data.name,
            icons: res.data.weather[0].icon
          });
          setError('');
          console.log(res.data);
        })
        //
        .catch(err => {
          if (err.response.status == 404) {
            setError("Invalid City Name!")
          }
          else {
            setError('')
          }
        });
    }
  }

  const [localTime, setLocalTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => {
      setLocalTime(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const utcTimestamp = localTime.getTime() + (localTime.getTimezoneOffset() * 60000);
  const offsetTimestamp = utcTimestamp + (data.timezone * 1000);
  const localTimeWithOffset = new Date(offsetTimestamp).toLocaleTimeString();

  const iconUrl = `https://openweathermap.org/img/wn/${data.icons}@2x.png`;

  return (
    <Flex className="App">
      {/* <select id="unit-select" value={unit} onChange={handleUnitChange}>
        <option value="metric">Metric</option>
        <option value="imperial">Imperial</option>
      </select> */}
      <div className="">
        <div>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Control
              type="email"
              placeholder="Enter email"
              type="text"
              placeholder="Enter city name"
              value={city}
              onChange={e => setCity(e.target.value)} />
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form.Group>
          <Form
            type="text"
            placeholder="Enter city name"
            className="w-full rounded-lg border-gray-200 p-4 pe-12 text-sm shadow-sm"
            value={city}
            onChange={e => setCity(e.target.value)}
          />
          <button onClick={handleClick}>Search</button>
        </div>

        <p>{error}</p>

        <p>{localTimeWithOffset}</p>

        <Title>
          {data.city}
        </Title>

        <img src={iconUrl} alt="" />


        {/* list tempterature */}
        <FlexRow>
          <Flex className="grey">
            <p className="m-0">LOW</p>
            <h2 className="m-0">
              {data.mintemp} °C
            </h2>
          </Flex>

          <Title className="m-0"> {data.temp} °C</Title>

          <Flex className="grey">
            <p className="m-0">HIGH</p>
            <h2 className="m-0">
              {data.maxtemp} °C
            </h2>
          </Flex>
        </FlexRow>

        <div className="icon-weather">
        </div>
        <FlexRow className="detail">
          <p>FEELS LIKE: {data.feels_like} °C</p>
          <p>HUMIDITY: {data.humidity} %</p>
          <p>WIND: {data.wind} m/s</p>
        </FlexRow>
      </div>
    </Flex>
  );
}

export default App;
