// import './App.css';
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Title = styled.h1`
  font-size: 2em;
`;

function App() {
  const [unit, setUnit] = useState('imperial')

  const handleUnitChange = (event) => {
    setUnit(event.target.value);
  };


  const [data, setData] = useState({
    temp: 10,
    name: 'Surakarta',
    humidity: 10,
    speed: 2
  })

  useEffect(() => {
    const url = `https://api.openweathermap.org/data/2.5/weather?q=surakarta&appid=281b1bf64dd901bf03d0cb8dbb2f18dd&units=${unit}`
    axios.get(url)
      .then(res => {
        setData({ ...data, temp: res.data.main.temp, name: res.data.name, humidity: res.data.main.humidity })
        console.log(res.data)
      })

      // 

      .catch(err => console.log(err));


  }, [])

  return (
    <div className="App">
      <select id="unit-select" value={unit} onChange={handleUnitChange}>
        <option value="metric">Metric</option>
        <option value="imperial">Imperial</option>
      </select>
      <div className="card">
        <p>Clock</p>
        <div className='temp'>
          <h4 className='grey'>1</h4>
          <Title className=''>{data.temp}</Title>
          <h4 className='grey'>1</h4>
        </div>
        <div className='icon-weather'><i>clud</i></div>
        <div className="detail">
          <p>FEELS LIKE: </p>
          <p>HUMIDITY: </p>
          <p>WIND: </p>
        </div>
      </div>
    </div>
  );
}

export default App;
