import React, { useState, useEffect } from "react";

function BigCities() {
    const [weatherData, setWeatherData] = useState([]);

    useEffect(() => {
        const cities = ["Tokyo", "New York", "Shanghai", "London", "Paris", "Los Angeles", "Moscow"];
        const unit = "metric"; // or "imperial" for Fahrenheit
        const urls = cities.map(city => `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=281b1bf64dd901bf03d0cb8dbb2f18dd&units=${unit}`);

        Promise.all(urls.map(url => axios.get(url)))
            .then(responses => {
                const data = responses.map(response => response.data);
                setWeatherData(data);
            })
            .catch(err => {
                if (err.response.status === 404) {
                    setError("Invalid City Name!");
                } else {
                    setError("");
                }
            });
    }, []);


    return (
        <div>
            {Array.isArray(weatherData) && weatherData.map(city => (
                <div key={city.id}>
                    <h2>{city.name}</h2>
                    <p>Current temperature: {city.main.temp}Â°C</p>
                    <p>Weather description: {city.weather[0].description}</p>
                    <p>Humidity: {city.main.humidity}%</p>
                    <p>Wind speed: {city.wind.speed} km/h</p>
                </div>
            ))}
        </div>
    );
}

export default BigCities;
