// import './App.css';
import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
import './App.css';
import { Button, Col, Container, Form, Row, InputGroup } from "react-bootstrap";
import { FiSearch } from 'react-icons/fi';



const Title = styled.h1`
  font-size: 2em;
`;

const Flex = styled.div`
    display: flex;
    text-align: center;
    align-items: center;
    justify-content: center;
    flex-direction: column;
`;

const FlexRow = styled.div`
    display: flex;
    align-items: flex-end;
    justify-content: center;
    gap: 16px;
`;

function App() {
  const [unit, setUnit] = useState('metric');

  const [data, setData] = useState({
    temp: 10,
    city: 'Jakarta',
    humidity: 10,
    speed: 2,
    timezone: 0,
    icons: ''

  });


  const handleClick = () => {
    if (city !== "") {
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=281b1bf64dd901bf03d0cb8dbb2f18dd&units=${unit}`;
      axios
        .get(url)
        .then(res => {
          setData({
            ...data,
            temp: res.data.main.temp,
            mintemp: res.data.main.temp_min,
            maxtemp: res.data.main.temp_max,
            humidity: res.data.main.humidity,
            timezone: res.data.timezone,
            feels_like: res.data.main.feels_like,
            wind: res.data.wind.speed,
            city: res.data.name,
            icons: res.data.weather[0].icon
          });
          setError('');
          console.log(res.data);
        })
        //
        .catch(err => {
          if (err.response.status == 404) {
            setError("Invalid City Name!")
          }
          else {
            setError('')
          }
        });
    }
  }
  const [city, setCity] = useState('');
  const [error, setError] = useState('');



  const [localTime, setLocalTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => {
      setLocalTime(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const utcTimestamp = localTime.getTime() + (localTime.getTimezoneOffset() * 60000);
  const offsetTimestamp = utcTimestamp + (data.timezone * 1000);
  const localTimeWithOffset = new Date(offsetTimestamp).toLocaleTimeString();

  const iconUrl = `https://openweathermap.org/img/wn/${data.icons}@2x.png`;

  return (
    <Flex className="App">
      {/* <select id="unit-select" value={unit} onChange={handleUnitChange}>
        <option value="metric">Metric</option>
        <option value="imperial">Imperial</option>
      </select> */}
      <Container fluid style={{
        backgroundImage: `url('https://images.unsplash.com/photo-1534274988757-a28bf1a57c17?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1035&q=80')`
      }}>
        <Row className="vh-100 text-white">
          <Col className="px-4">



            <div className="d-flex align-items-center gap-2 justify-content-center mt-5 flex-column">

              <InputGroup className="my-3">
                <Form.Control
                  className="border-0 border-bottom radius-0"
                  type="email"
                  placeholder="Enter email"
                  type="text"
                  placeholder="Enter city name"
                  value={city}
                  onChange={e => setCity(e.target.value)}
                />
                <Button className="border-0" variant="outline-secondary" id="button-addon1" onClick={handleClick}>
                  <FiSearch onClick={handleClick} />
                </Button>
              </InputGroup>
              {/* <Form.Group className="" controlId="formBasicEmail">
                <Form.Control
                  type="email"
                  placeholder="Enter email"
                  type="text"
                  placeholder="Enter city name"
                  value={city}
                  onChange={e => setCity(e.target.value)} />

              </Form.Group>
              <Button onClick={handleClick}>Search</Button> */}


              <p>{error}</p>

              <p>{localTimeWithOffset}</p>

              <Title>{data.city}</Title>

              {/* image */}
              <img src={iconUrl} alt="" />


              {/* list tempterature */}
              <FlexRow>
                <Flex className="text-muted">
                  <p className="m-0">LOW</p>
                  <h2 className="m-0">
                    {Math.round(data.mintemp)} 째C
                  </h2>
                  <p className="text-muted">HUMIDITY: {data.humidity} %</p>

                </Flex>
                <Flex>


                  <Title className="m-0"> {Math.round(data.temp)} 째C</Title>
                  <p className="text-muted">FEELS LIKE: {data.feels_like} 째C</p>

                </Flex>

                <Flex className="text-muted">
                  <p className="m-0">HIGH</p>
                  <h2 className="m-0">
                    {Math.round(data.maxtemp)} 째C
                  </h2>
                  <p className="text-muted">WIND: {data.wind} m/s</p>

                </Flex>
              </FlexRow>
            </div>
          </Col>

          <Col xs={8} className="bg-dark">
            asd</Col>
        </Row>
      </Container>
    </Flex >
  );
}

export default App;
